#!/bin/bash
# Bash completion script for Demo Node.js CLI (demo_js)
# Auto-generated by goobits-cli

_demo_js_completions() {
    local cur prev words cword
    _init_completion || return

    # Get the current CLI binary path
    local cli_bin="demo_js"
    
    # Check if CLI supports completion
    if ! command -v "$cli_bin" >/dev/null 2>&1; then
        return 0
    fi
    
    # Get completion suggestions from the CLI itself
    local suggestions
    suggestions=$("$cli_bin" _completion bash "$COMP_LINE" "$COMP_POINT" 2>/dev/null)
    
    if [[ $? -eq 0 && -n "$suggestions" ]]; then
        COMPREPLY=($(compgen -W "$suggestions" -- "$cur"))
        return 0
    fi
    
    # Fallback to static completions
    case "${prev}" in
        demo_js)
            # Top level commands
            local commands="greet info upgrade plugin help"
            COMPREPLY=($(compgen -W "$commands" -- "$cur"))
            return 0
            ;;
        greet)
            # Options for greet
            local options="--style -s --count -c --uppercase -u --language -l --help -h"
            COMPREPLY=($(compgen -W "$options" -- "$cur"))
            return 0
            ;;
        info)
            # Options for info
            local options="--format -f --verbose -v --sections -s --help -h"
            COMPREPLY=($(compgen -W "$options" -- "$cur"))
            return 0
            ;;
        plugin)
            local plugin_commands="list install uninstall create info help"
            COMPREPLY=($(compgen -W "$plugin_commands" -- "$cur"))
            return 0
            ;;
        upgrade)
            local upgrade_options="--check --version --pre --dry-run --help -h"
            COMPREPLY=($(compgen -W "$upgrade_options" -- "$cur"))
            return 0
            ;;
        --version|-V)
            # Don't complete after version flags
            return 0
            ;;
        --help|-h)
            # Don't complete after help flags
            return 0
            ;;
        *)
            # Global options
            local global_options="--help -h --version -V"
            
            # Check if we're in a subcommand context
            local i=1
            local in_subcommand=""
            while [[ $i -lt $cword ]]; do
                case "${words[$i]}" in
                    greet)
                        in_subcommand="greet"
                        break
                        ;;
                    info)
                        in_subcommand="info"
                        break
                        ;;
                    plugin|upgrade)
                        in_subcommand="${words[$i]}"
                        break
                        ;;
                esac
                ((i++))
            done
            
            if [[ -n "$in_subcommand" ]]; then
                # We're in a subcommand, offer subcommand-specific options
                case "$in_subcommand" in
                    greet)
                        local cmd_options="--style -s --count -c --uppercase -u --language -l --help -h"
                        COMPREPLY=($(compgen -W "$cmd_options" -- "$cur"))
                        ;;
                    info)
                        local cmd_options="--format -f --verbose -v --sections -s --help -h"
                        COMPREPLY=($(compgen -W "$cmd_options" -- "$cur"))
                        ;;
                    plugin)
                        local plugin_options="--json --global --version --type --dir --help -h"
                        COMPREPLY=($(compgen -W "$plugin_options" -- "$cur"))
                        ;;
                    upgrade)
                        local upgrade_options="--check --version --pre --dry-run --help -h"
                        COMPREPLY=($(compgen -W "$upgrade_options" -- "$cur"))
                        ;;
                esac
            else
                COMPREPLY=($(compgen -W "$global_options" -- "$cur"))
            fi
            return 0
            ;;
    esac

    # File completion for certain options
    case "${prev}" in
        --config|--file|-f)
            COMPREPLY=($(compgen -f -- "$cur"))
            return 0
            ;;
        --dir|--directory|-d)
            COMPREPLY=($(compgen -d -- "$cur"))
            return 0
            ;;
    esac

    return 0
}

# Register the completion function
complete -F _demo_js_completions demo_js

# Also register for common variations

# Support for npm global installation
if [[ -n "${NPM_CONFIG_PREFIX:-}" ]]; then
    complete -F _demo_js_completions "${NPM_CONFIG_PREFIX}/bin/demo_js"
fi