#!/usr/bin/env node
/**
 * Test Node.js CLI - Test CLI for Node.js validation
 * Generated by Goobits CLI Framework
 */

const { Command } = require('commander');
const hooks = require('./hooks');

const program = new Command();

program
    .name('testcli')
    .description('None')
    .version('1.0.0');

program
    .command('greet')
    .description('Greet someone')
    .option('----loud', 'Greet loudly')
    .action(async (options) => {
        try {
            const hookName = 'on_greet';
            if (typeof hooks[hookName] === 'function') {
                await hooks[hookName](options);
            } else {
                console.error(`Hook function '${hookName}' not implemented`);
                process.exit(1);
            }
        } catch (error) {
            console.error('Error:', error.message);
            process.exit(1);
        }
    });

program.parse();
