#!/usr/bin/env node
/**
 * Test TypeScript CLI - Test CLI for TypeScript validation
 * Generated by Goobits CLI Framework
 */

import { Command } from 'commander';
import * as hooks from './hooks';

const program = new Command();

program
    .name('testcli')
    .description('None')
    .version('1.0.0');

program
    .command('greet')
    .description('Greet someone')
    .option('----loud', 'Greet loudly')
    .action(async (options: any) => {
        try {
            const hookName = 'on_greet' as keyof typeof hooks;
            if (typeof hooks[hookName] === 'function') {
                await (hooks[hookName] as Function)(options);
            } else {
                console.error(`Hook function '${hookName}' not implemented`);
                process.exit(1);
            }
        } catch (error) {
            console.error('Error:', (error as Error).message);
            process.exit(1);
        }
    });

program.parse();