#!/usr/bin/env node
/**
 * Test CLI - A test CLI for universal templates
 * Generated by Goobits CLI Framework
 */

const { Command } = require('commander');
const hooks = require('./hooks');

const program = new Command();

program
    .name('test-cli')
    .description('Test CLI description')
    .version('1.0.0');
program.option(
    '--debug, -d',
    'Enable debug mode'
);

program
    .command('hello')
    .description('Say hello')
    .argument('name', 'Name to greet')
    .option('--uppercase, -u', 'Convert to uppercase')
    .action(async (name, options) => {
        try {
            const hookName = 'on_hello';
            if (typeof hooks[hookName] === 'function') {
                await hooks[hookName](name, options);
            } else {
                console.error(`Hook function '${hookName}' not implemented`);
                process.exit(1);
            }
        } catch (error) {
            console.error('Error:', error.message);
            process.exit(1);
        }
    });

program.parse();